general/variables.scss:
// Breakpoints
$breakpoint-xs: 480px;
$breakpoint-s: 1000px;
$breakpoint-m: 1024px;
$breakpoint-l: 1440px;
$breakpoint-xl: 1920px;

// Grid
$max-width: 1530px;
$font-size: 15px;
$gutter: 15px;
$double-gutter: $gutter * 2;
$border-radius: 4px;

$susy: (
  columns: 12,
  gutters: 0.25,
  debug: (
    image: hide,
    output: background,
  )
);

// Easing
$ease-out-quad: cubic-bezier(0.250, 0.460, 0.450, 0.940);
$ease-out-back: cubic-bezier(0.175, 0.885, 0.320, 1.275);

// Transitions
$transition-navbar: 0.2s linear;

// Alert Colours
$color-info: #00529B;
$color-success: #8BC53F;
$color-warning: #F3B603;
$color-error: #FF5E5D;
$color-alert-text: #fff;

// Colors
$color-topbar-sep: contrast(#000, 100%);

// Fonts
$roboto: 'Metropolis', sans-serif;;
$roboto-slab: 'Metropolis', sans-serif;;

// Measurements
$topbar-height: rem-calc(56px);
$navbar-height: rem-calc(100px);
$compressed-navbar-height: rem-calc(56);
$expanded-nav-height: $topbar-height + $navbar-height;
$modal-width-narrow: rem-calc(684px);
$section-padding: rem-calc(80px);
$blog-index-width: rem-calc(645px);
$mini-cart-width: 400px;

// Z Indices
$nav-mobile-z: 1000;

// Core
$account-light-color: $color-background;
$account-accent-color: $color-body;
$account-border-color: $color-border;
$account-link-color: $color-accent;
$account-gutter: $gutter;
$account-font-size: $font-size;

general/extends.scss:
/*----------  Text treatments  ----------*/

%body-text-treatment {
  font-family: $font-body;
  color: $color-body;
  font-size: $font-size;
  line-height: 1.666;
}

%heading-text-treatment {
  text-transform: uppercase;
  letter-spacing: 0.05em;
  font-family: $heading-font;
  font-weight: $heading-weight;
  color: $heading-color;
}

%page-header-treatment {
  background-color: $color-background-secondary;
  padding: rem-calc(40px) rem-calc(62px);
  text-align: center;
}

%section-title-text-treatment {
  @extend h3;
  text-align: center;
  color: $section-title-color;
}

%meta-text-treatment {
  text-transform: uppercase;
  letter-spacing: 0.05em;
  font-family: $meta-font;
  font-weight: $meta-weight;
  color: $color-meta;
}

%top-bar-font {
  text-transform: uppercase;
  font-size: 13px;
  font-weight: 300;
}

%link-text-treatment {
  padding-bottom: 2px;
  font-weight: 400;
  text-transform: uppercase;
  letter-spacing: 0.077em;
  font-size: rem-calc(13);
  text-decoration: underline;

  @if ($font-secondary == "Roboto Condensed") {
    font-family: $font-secondary;
  }

  &:hover {
    text-decoration: none;
    color: $color-link-hover;
  }
}

%list-style {
  padding-left: 1.25em;
  margin-bottom: 2em;

  li {
    margin-bottom: 1em;
  }
}

/*----------  Layout  ----------*/

%wrapper {
  @extend %wrapper-padding;
  margin-right: auto;
  margin-left: auto;
  max-width: $max-width;

  /* Solves border-box issue with max-width in IE8 */
  .lt-ie9 & { width: $max-width; }
}

%wrapper-padding {
  padding-left: 30px - $gutter;
  padding-right: 30px - $gutter;
}

%full-width {
  padding-left: 30px;
  padding-right: 30px;
}

%ghost-center {
  content: " ";
  display: inline-block;
  height: 100%;
  margin-right: -0.25em;
  vertical-align: middle;
}

/*----------  Forms  ----------*/

%form-base-style {
  max-width: 100%;
  margin-left: auto;
  margin-right: auto;
  background-color: $color-background-secondary;
  padding: 50px $gutter;
  margin-top: 56px;

  @include media($min: $breakpoint-s) {
    padding: 50px;
  }

  &:after {
    content: " ";
    display: block;
    clear: both;
  }
}

/*----------  Utils  ----------*/

%top-bar-sep {
  position: absolute;
  top: 50%;
  display: inline-block;
  width: 1px;
  height: 25px;
  margin-left: 18px;
  vertical-align: top;
  content: '';
  pointer-events: none;
  background-color: $color-border;
  transform: translateY(-50%);

  @if (lightness($color-topbar-background) < 70) {
    opacity: 0.3;
  }
}

%fill-parent {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
