modules/tabs.scss:
.tabs {
  margin-bottom: 55px;
  font-size: 0;
  text-align: center;
}

.tab-title {
  padding: 0;
  font-size: $font-size;
  border: 2px solid $color-border;

  &:hover {
    color: contrast($color-nav-active, 100%);
    background-color: $color-accent;
    border-color: $color-accent;
  }

  &.active {
    color: contrast($color-nav-active, 100%, 60);
    background-color: $color-nav-active;
    border-color: $color-nav-active;
  }

  &:not(:first-child) {
    margin-left: -1px;
  }

  &:first-child {
    border-radius: $border-radius;

    a { cursor: default; }

    &:not(:last-child) {
      border-radius: $border-radius 0 0 $border-radius;

      a { cursor: pointer; }
    }
  }

  &:last-child:not(:first-child) {
    border-radius: 0 $border-radius $border-radius 0;
  }

  a {
    display: block;
    min-width: 190px;
    padding: 14px;
    color: inherit;

    &:hover {
      color: inherit;
    }
  }
}

.tabs-content {
  position: relative;

  .icon-spinner {
    position: absolute;
    top: 0;
    left: 50%;
    margin-left: -10px;
  }
}

.tab-content-panel {
  &.active ~ .icon-spinner {
    display: none;
  }
}

modules/baguetteBox.scss:
/*!
 * baguetteBox.js
 * @author  feimosi
 * @version 1.4.1
 * @url https://github.com/feimosi/baguetteBox.js
 */

// scss-lint:disable QualifyingElement, IdSelector, SelectorFormat

%arrow-button {
  top: calc(50% - 30px);
  width: 44px;
  height: 60px;
}

#baguetteBox-overlay {
  position: fixed;
  top: 0;
  left: 0;
  z-index: 1000000;
  display: none;
  width: 100%;
  height: 100%;
  overflow: hidden;
  background-color: rgba($color-modal-background, 0.98);
  opacity: 0;
  transition: opacity 0.5s ease;

  &.visible { opacity: 1; }

  .full-image {
    position: relative;
    display: inline-block;
    width: 100%;
    height: 100%;
    text-align: center;

    figure {
      display: inline;
      height: 100%; // Opera 12 image stretching fix
      margin: 0;
    }

    img {
      display: inline-block;
      width: auto;
      height: auto;
      max-width: 100%;
      max-height: 90%;
      vertical-align: middle;
    }

    figcaption {
      position: absolute;
      bottom: 0;
      display: block;
      width: 100%;
      padding-bottom: 16px;
      font-weight: 300;
      line-height: 1.8;
      text-align: center;
      white-space: normal;
    }

    &:before {
      display: inline-block;
      width: 1px;
      height: 50%;
      margin-right: -1px;
      content: "";
    }
  }
}

#baguetteBox-slider {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  white-space: nowrap;
  transition: left 0.4s ease, transform 0.4s ease;

  &.bounce-from-right {
    animation: bounceFromRight 0.4s ease-out;
  }

  &.bounce-from-left {
    animation: bounceFromLeft 0.4s ease-out;
  }
}

@keyframes bounceFromRight{
  0% { margin-left: 0; }
  50% { margin-left: -30px; }
  100% { margin-left: 0; }
}

@keyframes bounceFromLeft{
  0% { margin-left: 0; }
  50% { margin-left: 30px; }
  100% { margin-left: 0; }
}

.baguetteBox-button {
  position: absolute;
  padding: 0;
  margin: 0;
  font-size: 1.6em;
  cursor: pointer;
  background-color: transparent;
  border: 0;
  border-radius: 15%;
  outline: none;
  transition: background-color 0.4s ease;

  &#next-button {
    @extend %arrow-button;
    right: 2%;
  }

  &#previous-button {
    @extend %arrow-button;
    left: 2%;
  }

  &#close-button {
    top: 20px;
    right: 20px;
    width: 30px;
    height: 30px;
  }

  // Firefox fix
  svg {
    position: absolute;
    top: 0;
    left: 0;

    g {
      stroke: contrast($color-modal-background, 100%);
    }
  }
}

// http://tobiasahlin.com/spinkit/
.spinner:not(.button) {
  position: absolute;
  top: 50%;
  left: 50%;
  display: inline-block;
  width: 40px;
  height: 40px;
  margin-top: -20px;
  margin-left: -20px;
}

.double-bounce1,
.double-bounce2 {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  border-radius: 50%;
  opacity: 0.6;
  animation: bounce 2s infinite ease-in-out;
}

.double-bounce2 { animation-delay: -1s; }

@keyframes bounce {
  0%, 100% { transform: scale(0); }

  50% { transform: scale(1); }
}

modules/meganav.scss:
.mega-nav-variant-container {
  display: flex;
  position: relative;
  width: 100%;
  padding-right: 12px;
  box-sizing: border-box;
  height: 400px;
}

.mega-nav-variant-container::-webkit-scrollbar {
  width: 12px;
}

.mega-nav-variant-container::-webkit-scrollbar-track {
  background: transparent;
}

.mega-nav-variant-container::-webkit-scrollbar-thumb {
  background: #888;
  border-radius: 6px;
}

.mega-nav-variant-container::-webkit-scrollbar-thumb:hover {
  background: #555;
}

.mega-nav-root-list {
  flex: 0 0 25%; /* Set the root list container to 30% width */
  border-right: 1px solid contrast($color-topbar-background, 10%);
  display: flex;
  flex-direction: column;
  font-size: rem-calc(16px);
  font-weight: 400;
  height: 100%;
  overflow-y: auto;
  text-transform: none;
  box-sizing: border-box;
  z-index: 1;

  .hovered {
    transition: background-color 0.3s ease-in-out;
    background-color: #f0f0f0;
  }

  .hovered a {
    color: $color-accent;
  }

  @include media($max: 1300px) {
    width: 30%; /* Adjust the width accordingly for smaller screens */
  }

  li {
    display: flex;
    align-items: center;
    margin-bottom: 10px; /* Increased spacing between items */
    padding: 8px; /* Added padding for a larger hover area */
    border-radius: 6px; /* Increased border-radius for larger corners */
    transition: background-color 0.3s ease, box-shadow 0.3s ease; /* Smooth transition */

    a {
      display: block;
      width: 100%; /* Ensure the anchor takes full width */
      word-break: break-word; /* Ensure long words break correctly */
      white-space: normal; /* Allow text to wrap */
      text-decoration: none; /* Remove underline */
      color: #333; /* Darker color for better readability */
      font-weight: 500; /* Slightly bolder font for emphasis */
    }

    &:hover {
      cursor: pointer;
      background-color: #f0f0f0; /* Light grey background on hover */
      box-shadow: 0 0 15px rgba(0, 0, 0, 0.1); /* Subtle shadow on hover */
    }
  }
}
@media only screen and (max-width: 1700px) {
  .mega-nav-root-list{
    font-size: rem-calc(12px);
  }
}
.mega-nav-child-container {
  flex: 1 1 75%; /* Set the child list container to 70% width */
  padding-left: rem-calc(30px);
  height: 100%;
  overflow-y: auto;
  box-sizing: border-box;
  position: relative;
}

.mega-nav-child-list-container {
  display: none;
  transition: opacity 0.3s ease-in-out;
  height: 100%;
  overflow-y: visible;
  position: relative;

  &.animating,
  &.visible {
    display: block;
    opacity: 1;
  }

  &.animating {
    position: absolute;
  }

  &.animating-in,
  &.animating-out {
    opacity: 0;
    transform: translateY(-10px);
    transition: opacity 0.2s ease, transform 0.1s ease;
  }

  &.visible {
    opacity: 1;
    transform: translateY(0);
  }
}

.mega-nav-child-header {
  display: block;
  font-family: $roboto;
  color: contrast($color-topbar-background, 100%);
  margin-top: 0;
  margin-bottom: rem-calc(10px);
  padding: 0;
  text-align: left;
  width: 100%;
}

.mega-nav-child-list-back {
  display: block;
  height: rem-calc(20px);
  margin-bottom: rem-calc(15px);
  text-align: left;
  font-size: 12px;
  margin-left: -7px;

  svg {
    margin-top: -3px;
    vertical-align: middle;
    transform: rotate(90deg) !important;
    color: contrast($color-topbar-background, 100%);

    path {
      transition: all 0.2s $ease-out-quad;
    }
  }

  a {
    height: 100%;
    color: contrast($color-topbar-background, 100%);
    text-transform: uppercase;
    text-decoration: underline;
    transition: all 0.2s $ease-out-quad;
  }

  &:hover {
    a {
      text-decoration: none;
      color: $color-link-hover;
    }

    svg path {
      fill: $color-link-hover;
    }
  }
}

.mega-nav-child-list-inner-container {
  display: flex;
  height: 100%;
}

.mega-nav-child-list {
  display: flex;
  max-height: 100%;
  flex-wrap: wrap;
  flex-direction: column;
  float: left;
  font-family: $roboto-slab;
  font-size: 13px;
  transition: margin-left 0.3s ease;
  padding-right: 12px;
}

.mega-nav-arrow {
  display: inline-block;
  height: 100%;
  margin-left: rem-calc(2px);
  margin-top: 3px;
  vertical-align: top;
  width: rem-calc(13px);

  .icon-arrow-down {
    transform: rotate(-90deg);
    vertical-align: middle;

    path {
      fill: $color-nav;
    }
  }
}

modules/carousel.scss:
/*----------  Carousel  ----------*/

.carousel {
  cursor: ew-resize;
  cursor: grab;

  .js & .carousel-item {
    opacity: 0;
    transition: opacity 0.6s ease;
  }

  .js &.slick-initialized .carousel-item {
    opacity: 1;
  }

  .container {
    position: relative;

    @include media($max: $breakpoint-s) {
      padding: 0;
    }
  }
}

.carousel-item {
  width: 100%;

  > a {
    display: block;
  }
}

.carousel-item-image {
  @include maintain-ratio(2.4 1);
  background-size: cover;

  .ratio-natural & {
    height: auto;
    padding-bottom: 0;
  }

  .ratio-3-1 & {
    @include maintain-ratio(3 1);
  }

  .ratio-16-9 & {
    @include maintain-ratio(16 9);
  }
}

.slick-slide {
  .home-carousel-image  {
    @extend .sr-only;

    .ratio-natural & {
      position: static !important; // override !important in .sr-only
      display: block;
      width: 100%;
      height: auto;
    }
  }

  .product-images-container & {
    height: auto !important; // override height from JS causing slides to be given incorrect height
  }
}

.carousel-item-info {
  padding: 37px 0;
  text-align: center;
  opacity: 0;
  transform: translate(0, 100%);
  transition: all 0.2s ease-out;
  background-color: rgba($color-carousel-background, 0.8);

  @include media($min: $breakpoint-s) {
    position: absolute;
    left: $gutter * 3;
    bottom: 0;
    width: 380px;
    padding: 25px 31px 32px;
    text-align: left;
  }

  &.show {
    opacity: 1;
    transform: translate(0, 0);
  }

  .carousel-item-title {
    margin: 0;
    font-family: $font-secondary;
    font-weight: 400;
    font-size: 40px;
    color: $color-carousel-title;
  }

  .carousel-item-caption {
    font-size: 15px;
    color: rgba($color-carousel-text, 0.75);
  }

  .carousel-item-link {
    font-size: 13px;
    color: $color-accent;
  }
}

.carousel-navigation-item {
  position: absolute;
  top: 50%;
  z-index: 1;
  padding: 1rem;
  color: $color-carousel-navigation;
  cursor: pointer;
  transform: translateY(-50%);

  @include media($max: $breakpoint-s) {
    display: none !important;
  }

  &.previous { left: 0; }
  &.next { right: 0; }

  .icon {
    height: 24px;
    width: 15px;
  }
}

.slick-dots {
  position: absolute;
  // Calculate the ratio of 2.4:1 and convert it to a viewport width
  top: (1 / 2.4 * 100) * 1vw;
  left: 50%;
  transform: translateX(-50%) translateY(-34px);
  list-style: none;
  margin: 0 0 30px 0;
  line-height: 1;
  text-align: center;

  @include media($min: $breakpoint-s) {
    top: auto;
  }

  // Calculate the ratio of 3:1 and convert it to a viewport width
  .ratio-3-1 & { top: (1 / 3 * 100) * 1vw; }

  // Calculate the ratio of 16:9 and convert it to a viewport width
  .ratio-16-9 & { top: (9 / 16 * 100) * 1vw; }

  li {
    display: inline-block;
    width: 16px;
    height: 16px;
    border: 2px solid $color-carousel-navigation;
    border-radius: 50%;
    margin: 0 4px;
    cursor: pointer;
    transition: all 0.3s ease;

    &:hover {
      background-color: $color-accent;
      border-color: $color-accent;
    }
  }

  .slick-active {
    border-color: $color-accent;
  }

  button {
    display: none;
  }
}
.product-images-container {
  position: relative;
  max-width: 100%;
  margin: 0 auto;
  overflow: hidden;
}

general/general.scss:
/* General */

body {
  @extend %body-text-treatment;
  background-color: $color-background;

  &.scroll-locked { overflow: hidden; }
}

/* Links */

a {
  color: $color-link;
  transition: color 0.2s $ease-out-quad;

  &:hover {
    color: $color-link-hover;
  }
}

.link {
  @extend %link-text-treatment;
}

/* Headers */

h1,
h2,
h3,
h4,
h5,
h6 {
  line-height: 1.25;

  a {
    color: inherit;

    &:hover {
      color: $color-accent;
    }
  }
}

h1,
h3,
h5 {
  font-weight: 400;
}

h1,
h3,
h5 {
  @if ($font-secondary == "Roboto Condensed") {
    text-transform: uppercase;
  }

  font-family: $font-secondary, 'Metropolis', sans-serif;;
  color: $color-bold;
}

h2,
h4 {
  font-family: $font-primary;
  font-weight: 300;
  text-transform: none;
}

h1 { font-size: rem-calc(36); }
h2 { font-size: rem-calc(28); }
h3 { font-size: rem-calc(24); }
h4 { font-size: rem-calc(20); }
h5 {
  @if ($font-secondary == "Roboto Condensed") {
    font-weight: 700;
  }

  font-size: rem-calc(17);
}

h6 {
  @if ($font-secondary == "Roboto Condensed") {
    font-family: $font-secondary;
  }

  text-transform: uppercase;
  font-size: rem-calc(13);
  font-weight: 700;
  letter-spacing: 0.0625em;
  color: $color-bold;
}

/* Utility classes */

.text-center {
  text-align: center;
}

.sr-only {
  @extend .show-for-sr;
}

.hidden {
  display: none;
}

/* Basic elements */

hr {
  background: $color-border;
  height: 1px;
}

cite {
  color: $color-meta;
}

img {
  max-width: 100%;
  height: auto;

  // Hide lazy-loading images while they are loading
  &:not([src]) {
    visibility: hidden;
  }
}

svg {
  fill: currentColor;
}

.list-bulleted {
  margin: 1em 0 1em 1.5em;
  list-style: disc;
}

general/button.scss:
/* Buttons */
button {
  border: 0;
}

.button {
  padding: 1.0625em;
  font-family: $button-font;
  font-size: 16px;
  font-weight: $button-weight;
  line-height: 1.3125; // 21/16
  color: $color-button;
  background-color: $color-button-background;
  border-radius: 4px;
  box-shadow: 0 0 0 $border-size transparent inset;
  transition: all 0.2s $ease-out-quad;

  @include media($min: $breakpoint-s) {
    &:hover {
      color: $color-button;
      background-color: $color-button-background-hover;
    }
  }

  &.inverse {
    @extend %button-inverse;
  }

  &.button-wide {
    padding-left: 3em;
    padding-right: 3em;
  }

  &.button-small {
    padding: 0.25em 0.5em;
    font-size: 13px;
  }

  &.button-short {
    padding: 0.75em 1.5em;
  }

  &.button-secondary {
    color: $color-button-secondary;
    background: $color-button-secondary-background;
    box-shadow: 0 0 0 $border-size $color-button-secondary inset;

    &:hover {
      color: lighten($color-button-secondary, 100%);
      background-color: $color-button-secondary;
    }

    &.inverse {
      @extend %button-secondary-inverse;
    }
  }

  &.button-tertiary {
    color: $color-button-tertiary;
    background: $color-button-tertiary-background;

    &:hover {
      color: $color-button-tertiary-background;
      box-shadow: 0 0 0 $border-size $color-button-tertiary-background inset;
      background-color: transparent;
    }
  }

  &:disabled,
  &.button-disabled {
    cursor: not-allowed;
    opacity: 0.6;
    background-color: $color-button-background-hover;
  }

  &.spinner {
    position: relative;

    &.progress {
      .button-text {
        opacity: 0.1;
      }
    }
  }

  .icon-spinner {
    position: absolute;
    top: 50%;
    left: 50%;
    width: 20px;
    height: 20px;
    margin: -10px 0 0 -10px;
  }
}

.button-group {
  @extend %clearfix;
  display: inline-block;
}

.button-group-item {
  position: relative;
  float: left;
  height: 45px;
  min-width: 45px;
  padding: 12px;
  font-family: $font-secondary;
  font-size: rem-calc(13px);
  line-height: 1;
  color: $color-meta;
  text-align: center;
  background-color: transparent;
  border: 2px solid $color-border;
  transition: all 0.3s ease;

  + .button-group-item {
    margin-left: -2px;
  }

  &:hover {
    background-color: $color-accent;
    border-color: $color-accent;
    color: contrast($color-nav-active, 100%);
  }

  &:first-child {
    border-radius: $border-radius 0 0 $border-radius;
  }

  &:last-child {
    border-radius: 0 $border-radius $border-radius 0;
  }

  &.active {
    z-index: 1;
    color: contrast($color-nav-active, 100%, 60);
    background-color: $color-nav-active;
    border-color: $color-nav-active;
  }

  &.disabled,
  &.active {
    pointer-events: none;
    cursor: default;
  }

  .icon-grid-view {
    width: 16px;
    height: 16px;
  }

  .icon-list-view {
    width: 17px;
    height: 16px;
  }
}

general/form.scss:
/*----------  Forms  ----------*/

.form-field {
  margin-bottom: rem-calc(28);
}

.form-label {
  font-weight: 400;

  &.checkbox {
    .form-label-text {
      margin-bottom: 0;
    }
  }
}

.form-input {
  display: block;
  padding: rem-calc(19 17 18);
  color: $color-input;
  border: 0;
  border-radius: $border-radius;
  transition: border-color 0.2s $ease-out-quad;

  @if ($font-secondary == "Roboto Condensed") {
    font-family: $font-secondary;
  }

  &:not([type="radio"]):not([type="checkbox"]):not(.quantity-input) {
    box-shadow: 0 0 0 $border-size $color-border inset;
  }

  &:not(textarea):not([type="radio"]):not([type="checkbox"]) {
    height: rem-calc(55);
  }

  &::placeholder {
    @extend %placeholder;
  }

  &:focus {
    outline: none;
  }

  &.form-input-short {
    padding: rem-calc(14 17 13);
    height: rem-calc(45);
  }
}

.form-radio,
.form-checkbox {
  display: inline-block;
  vertical-align: middle;

  + .form-label-text {
    display: inline-block;
    font-weight: 300;
    margin-bottom: rem-calc(10);
    margin-left: rem-calc(10);
  }
}

.form-select-wrapper {
  position: relative;
  display: block;

  &:after {
    @include icon-arrow-down($color-meta);
    position: absolute;
    top: 50%;
    right: 20px;
    display: inline-block;
    width: 9px;
    height: 6px;
    margin-top: -3px;
    pointer-events: none;
    content: "";
  }

  .form-select { width: 100%; }
}

.form-select {
  height: rem-calc(55);
  padding: 0 rem-calc(36) 0 rem-calc(17);
  background-color: $color-background;
  appearance: none;

  // For IE 10+
  &::-ms-expand { display: none; }

  &.form-input-short {
    height: rem-calc(45);
    padding: 0 rem-calc(36) 0 rem-calc(17);
  }
}

.form-label-text,
.form-field-title {
  display: block;
  margin: 0 rem-calc(10) rem-calc(8px) 0;
  font-weight: normal;
}

.form-field-title {
  .required-text {
    display: none;

    .form-required & {
      display: inline-block;
    }
  }
}

.input-group {
  @include layout-group;
}

.input-group-item {
  @include layout-group-item;
  vertical-align: top;

  &:first-child {
    .button {
      border-radius: $border-radius 0 0 $border-radius;
      margin-right: -2px;
    }
  }

  &:last-child {
    .button {
      border-radius: 0 $border-radius $border-radius 0;
      margin-left: -2px;
    }
  }

  &.form-field {
    width: 99%;
  }

  .form-input {
    width: 100%;
  }
}

// ---------------------------------------------------------------- */
// Date Field Components

.form-field-date {
  @extend %clearfix;

  .form-select-wrapper { float: left; }

  .form-date-year { width: 44%; }

  .form-date-month {
    width: 25%;
    margin-right: 3%;
    margin-left: 3%;
  }

  .form-date-day { width: 25%; }
}

/*----------  Form Errors  ----------*/

.form-error {
  color: $color-error;
  border: 1px solid $color-error;

  &::placeholder {
    color: lighten($color-error,30%);
  }
}

.form-error-message {
  color: $color-error;
}

.form-inline-message {
  display: block;
  margin-top: rem-calc(10);
  clear: left;

  .form-field-invalid & {
    color: $color-error;
  }
}

general/table.scss:
/* Tables */

table {
  color: $color-body;
  border-color: $color-border;
}

th {
  @extend %heading-text-treatment;
  color: $color-input;
}

table.geotrust-ssl-seal-table {
  margin-top: 30px;
  margin-bottom: 0;
  border: 0;

