components/search.scss:
/*----------  Search Form  ----------*/

.search-form {
  position: relative;
  max-width: 250px;
  margin: rem-calc(22px) auto 0;

  .search-input {
    width: 100%;
  }

  .button-search-submit {
    position: absolute;
    top: 50%;
    right: $gutter;
    line-height: 0;
    color: $color-meta;
    background: none;
    transform: translateY(-50%);
  }

  .icon-search {
    width: 24px;
    height: 24px;
  }

  .form-inline-message {
    position: absolute;
    bottom: rem-calc(-50);
  }
}

.search-wrap {
  position: fixed;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 1000;
  color: #fff;
  background-color: rgba($color-modal-background, 0.98);
  transform: translateY(-100%);
  transition: all 0.2s ease-out;

  &.search-open {
    top: 0;
    height: 100vh;
    opacity: 1;
    transform: translateY(0);
  }

  .icon {
    fill: currentColor;
  }

  .button-search-close {
    @extend .button-modal-close;

    &:hover {
      color: $color-accent;
    }
  }

  .search-form {
    position: absolute;
    top: 50%;
    left: 50%;
    width: 100%;
    max-width: 586px;
    padding-bottom: rem-calc(10);
    transform: translate(-50%, -50%);
    border-bottom: 1px solid rgba(contrast($color-modal-background, 100%), 0.2);

    .search-input.form-input {
      box-shadow: none;
    }
  }

  .search-input {
    @extend h4;
    padding-right: 0;
    padding-left: 0;
    background: none;
    color: contrast($color-modal-background, 100%);

    &::placeholder {
      color: contrast($color-modal-background, 100%);
      text-transform: none;
      font-size: rem-calc(20);
    }

    &:focus {
      outline: none;
    }
  }

  .button-search-submit {
    top: rem-calc(25);
    color: contrast($color-modal-background, 100%);
  }

  .form-field {
    margin: 0;
  }
}

.button-search-submit {
  float: right;
  right: 0;
  background: none;

  &:hover,
  &:focus {
    color: $color-accent;
    outline: 0;
  }

  .icon {
    width: 25px;
    height: 25px;
  }
}

/*----------  Search Page  ----------*/

.search {
  .product-listing {
    padding-top: 0;
  }

  .tab-content-panel {
    display: grid;

    &.with-sidebar {
      @include media($min: $breakpoint-s) {
        grid-template-columns: 1fr minmax(0, 3fr);
        column-gap: 2%;
        align-items: start;
      }
    }
  }
}

.tabs {
  margin-top: rem-calc(80);
}

.content-results {
  margin-bottom: rem-calc(80);
}

.content-result {
  margin-bottom: rem-calc(40);

  h1 {
    @extend h4;
  }
}

.content-results-pagination {
  text-align: center;
}

components/mini-cart.scss:
.mini-cart {
  position: fixed;
  top: 0;
  right: -400px;
  width: 400px;
  height: 100%;
  background-color: $color-mini-cart-background;
  transition: transform .3s ease;

  @if ($color-mini-cart-background == $color-background) {
    box-shadow: 1px 0 0 0 $color-border inset;
  }

  .no-js & { overflow: auto; }

  .button-checkout {
    @extend %button-inverse;
  }
}

.mini-cart-inner {
  position: relative;
  top: 0;
}

.mini-cart-top-bar {
  @include white-text($color-mini-cart-background);

  .button-cart-close {
    position: absolute;
    top: rem-calc(40);
    right: rem-calc(25);
    padding: 0;
    background: none;

    .icon-close {
      &:hover {
        color: $color-accent;
      }
    }
  }
}

.mini-cart-contents {
  > .icon-spinner {
    display: block;
    margin: rem-calc(20) auto;
  }
}

.mini-cart-title {
  @include white-text($color-mini-cart-background);
  font-size: 18px;
  text-align: center;
  font-family: $font-primary;
  font-weight: 300;
  text-transform: none;
  margin: 0;
  padding: rem-calc(40) 0;
}

.mini-cart-empty {
  @include white-text($color-mini-cart-background);
  text-align: center;
}

.mini-cart-item {
  @include white-text($color-mini-cart-background);
  position: relative;
  transition: background-color 0.3s ease;

  &:not(:last-of-type) {
    &:after {
      display: block;
      width: calc(100% - 50px);
      height: 1px;
      margin-left: 25px;
      background-color: rgba(contrast($color-mini-cart-background, 100%), 0.05);
      content: "";
    }
  }

  &:hover {
    background-color: darken($color-mini-cart-background, 5%);

    &:after {
      background-color: darken($color-mini-cart-background, 5%);
    }

    .mini-cart-item-actions {
      opacity: 1;
    }
  }

  &.removing {
    .mini-cart-item-wrap {
      pointer-events: none;
      opacity: 0.2;
    }
  }

  a {
    color: inherit;
  }

  .icon-spinner {
    position: absolute;
    top: 25px;
    right: 25px;
  }
}

.mini-cart-item-wrap {
  @include layout-group();
  padding: 25px;

  & > * {
    @include layout-group-item();
  }

  .giftcard-image {
    width: 75px;
  }
}

.mini-cart-item-thumb {
  position: relative;
  width: 75px;

  &.no-image {
    background-color: $color-background;

    @if ($color-mini-cart-background == $color-background) {
      box-shadow: 1px 0 0 0 $color-border inset;
    }
  }
}

.mini-cart-item-image {
  @include lazy-loaded-img;
  width: 100%;
  height: 100%;
}

.mini-cart-item-title {
  font-family: $font-secondary, 'Metropolis', sans-serif;
  font-weight: bold;
  font-size: 17px;
  text-transform: uppercase;
}

.mini-cart-item-options {
  padding-top: 2px;
  padding-bottom: 2px;

  .option-value {
    margin-right: 4px;
    color: $color-body;

    &:not(:last-child):after {
      content: ",";
    }
  }
}

.mini-cart-item-description {
  vertical-align: middle;
  padding-left: 20px;
  width: 90%;
}

.mini-cart-item-actions {
  font-size: 12px;
  opacity: 0;
  transition: opacity 0.3s ease;

  .link {
    margin-right: 14px;
  }
}

.mini-cart-item-price {
  text-align: right;
  vertical-align: bottom;
  opacity: 0.6;

  .price-discounted { text-decoration: line-through; }
}

.mini-cart-footer {
  padding: 25px;
  border-top: 1px solid rgba(contrast($color-mini-cart-background, 100%), 0.05);

  .mini-cart-subtotal {
    float: right;
    font-size: 111%;
    vertical-align: middle;
  }
}

.mini-cart-footer-title {
  color: contrast($color-mini-cart-background, 100%);
}

.mini-cart-actions {
  .button {
    width: calc(50% - 5px);

    & + .button { float: right; }
  }

  .button-secondary {
    @include white-text($color-mini-cart-background);

    @if lightness($color-mini-cart-background) < 33 {
      box-shadow: 0 0 0 2px contrast($color-mini-cart-background, 100%) inset;
    }

    &:hover {
      box-shadow: 0 0 0 2px $color-button-secondary inset;
    }
  }
}

components/navigation-mobile.scss:
.nav-mobile-open {
  .site-canvas {
    transform: none;
    padding-top: $topbar-height;
  }

  .top-bar {
    position: fixed;
    z-index: $nav-mobile-z;
    top: 0;
    left: 0;
    width: 100%;
    background-color: blue;
  }

  .button-mobile-nav-toggle {
    .burger {
      transform: rotate(-45deg);
      &:before { transform: rotate(90deg); }
      &:after { display: none; }
    }
  }
}

.button-mobile-nav-toggle {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);

  &.has-promo {
    top: auto;
    bottom: 15px;
    transform: translateY(0);
  }
}

.button-mobile-nav-back {
  position: relative;
  bottom: 15px;
  left: -5px;
  display: none;
  color: contrast($color-topbar-background, 100%);
  background: transparent;

  .icon {
    width: 9px;
    height: 14px;
  }

  .mobile-nav-open & {
    display: block;
  }
}

.button-mobile-nav-toggle {
  left: 10px;
  height: 28px;

  @include media($min: $breakpoint-s) { display: none; }

  &:focus { outline: none; }

  .burger {
    display: block;
    width: 16px;
    height: 2px;
    background: contrast($color-topbar-background, 100%);
    border-radius: 1px;
    transition: transform 0.2s ease;

    &:before,
    &:after {
      display: block;
      width: 16px;
      height: 2px;
      background: contrast($color-topbar-background, 100%);
      border-radius: 1px;
      content: '';
      transition: transform 0.2s ease;
    }

    &:before {
      transform: rotate(0deg) translate3d(0, -5px, 0);
    }

    &:after {
      transform: translateY(3px);
    }
  }
}

.button-mobile-nav-cart {
  position: absolute;
  top: 50%;
  right: 15px;
  height: 28px;
  transform: translateY(-38%);

  &.has-promo {
    top: auto;
    bottom: 8%;
    transform: translateY(0);
  }

  @include media($min: $breakpoint-s) { display: none; }

  a {
    display: block;
    height: 100%;
    vertical-align: middle;
    text-align: center;

    svg {
      fill: contrast($color-topbar-background, 100%);
    }
  }
}

.navigation-mobile {
  position: fixed;
  z-index: $nav-mobile-z;
  top: $topbar-height;
  width: 100%;
  height: calc(100vh - 56px);
  display: none;
  overflow-y: auto;
  background-color: rgba($color-topbar-background, 0.97);
  border-top: 1px solid rgba(#fff, 0.1);
  -webkit-overflow-scrolling: touch;

  @include media($min: $breakpoint-s) {
    display: none !important;
  }

  &.animating,
  &.visible {
    display: block;
  }

  &.animating-in,
  &.animating-out {
    transform: translateX(-100%);
    transition: transform 0.3s ease;
  }

  &.visible {
    transform: translateX(0);
    opacity: 1;
  }

  .search-form {
    position: relative;
    display: block;
    padding: 10px;
    max-width: none;
  }

  .search-input {
    @extend .form-input;
    width: 100%;
  }

  .button-search-submit {
    position: absolute;
    right: 27px;
    top: 38px;
    padding: 0;
    background: none;

    .icon-search {
      width: 16px;
      height: 16px;
    }
  }

  a { padding: 9px 0; }

  .navigation {
    padding: 20px;
    float: none;
    border-bottom: 1px solid rgba($color-topbar-text, 0.1);
    transition: transform 0.2s ease;

    li,
    a { display: block; }

    .parent > a { color: contrast($color-modal-background, 60%); }
  }

  .nav-mobile-item,
  .nav-mobile-item a,
  .button-util,
  .currency-selector .dropdown-toggle,
  .children a {
    font-family: $font-secondary;
    font-size: rem-calc(16);
    color: $color-topbar-text;
    text-transform: uppercase;
  }

  .nav-mobile-panel-child {
    .nav-mobile-item a {
      @extend %body-text-treatment;
      color: contrast($color-modal-background, 60%);
      text-transform: none;
    }
  }

  .utils {
    padding: 20px;

    a { display: block; }

    .mobile-account-utils {
      display: inline-block;
    }
  }

  .icon-account,
  .icon-wishlist,
  .icon-cart {
    width: 18px;
    margin-right: 11px;
  }

  .dropdown-panel {
    position: static;
    padding-top: 0;
  }

  .currency-selector {
    margin-left: 0 !important; // override all styles for mobile

    .dropdown-toggle.top-bar-button {
      height: auto;
      padding-left: 0;
    }

    .dropdown-panel {
      background-color: transparent;
      padding-top: 0;
    }
  }

  .icon-arrow-down {
    margin-top: -0.05rem;
    transform: rotate(270deg);
  }
}

// Mobile nav styles
.nav-mobile-container {
  position: relative;
  overflow: hidden;
}

.nav-mobile-panel {
  width: 100%;
  transition: transform 0.5s ease;

  &.is-active {
    position: relative;
  }

  &.is-right,
  &.is-left {
    position: absolute;
    top: 0;
  }

  &.is-right {
    transform: translateX(100%);
  }

  &.is-left {
    transform: translateX(-100%);
  }

  &.force-state {
    transition: none;
  }

  .nav-mobile-panel-title {
    margin-bottom: 0.5rem;
  }
}

components/products/product-listing.scss:
.product-listing {
  &.section {
    @include media($max: $breakpoint-s) {
      padding-top: 0;
    }
  }

  .container {
    display: grid;

    &.with-sidebar {
      @include media($min: $breakpoint-s) {
        grid-template-columns: 1fr minmax(0, 3fr);
        column-gap: 2%;
        align-items: start;
      }
    }
  }
}

/*----------  Category Banner  ----------*/

.category-banner {
  .container {
    position: relative;

    @include media($max: $breakpoint-s) {
      background-color: $color-background-secondary;
    }
  }
}

.category-banner-image {
  @include maintain-ratio(2 1);
  background-repeat: no-repeat;
  background-size: cover;

  .banner-ratio-smallest & { @include maintain-ratio(8 1); }
  .banner-ratio-small & { @include maintain-ratio(6 1); }
  .banner-ratio-medium & { @include maintain-ratio(4 1); }
}

.category-banner-description {
  padding: rem-calc(37 0);
  text-align: center;

  @include media($min: $breakpoint-s) {
    position: absolute;
    left: $gutter;
    bottom: 0;
    width: rem-calc(380);
    padding: rem-calc(25 31 32);
    background-color: rgba($color-carousel-background, 0.8);
    color: contrast($color-carousel-background, 100%);
    text-align: left;
  }
}


/*----------  Listing Header  ----------*/

.listing-header {
  display: flex;
  justify-content: center;
  margin: 0 -$gutter;
  margin-bottom: $double-gutter;
  background-color: $color-background-secondary;

  @include media($min: $breakpoint-s) {
    grid-column: 1 / -1;
    margin-left: 0;
    margin-right: 0;
    background-color: transparent;
  }

  .listing-utils {
    margin-left: auto;

    @include media($max: $breakpoint-s) {
      display: none;
    }
  }
}

.listing-title {
  @extend h3;
}

.listing-description {
  clear: left;
}

.listing-utils {
  float: right;

  .sorting,
  .button-group,
  .form-label,
  .form-select-wrapper {
    display: inline-block;
    vertical-align: middle;
    margin-left: 16px
  }

  .form-field {
    margin-bottom: 0;
    margin-right: 10px;
  }

  .button-group {
    @include media($max: $breakpoint-s) {
      display: none;
    }
  }
}

[data-content-region] {
  grid-column: 1 / -1;
}

/*----------  Listing Grid  ----------*/

.product-listing-grid {
  display: flex;
  flex-direction: column;

  .listing-view {
    .js & {
      display: none;
    }

    &.active {
      display: block;
    }
  }

  .listing-wrapper {
    display: grid;

    @include media($min: $breakpoint-xs) {
      grid-template-columns: repeat(2, minmax(0, 1fr));
      column-gap: 1.5%;
    }

    @include media($min: $breakpoint-s) {
      grid-template-columns: repeat(3, minmax(0, 1fr));
    }

    @include media($min: $breakpoint-m) {
      grid-template-columns: repeat(4, minmax(0, 1fr));
    }

    &.with-sidebar {
      @include media($min: 100px) {
        grid-template-columns: repeat(2, minmax(0, 1fr));
        gap: 15px;
      }

      @include media($min: 1000px) {
        grid-template-columns: repeat(3, minmax(0, 1fr));
      }
    }

    &.list-view {
      display: flex;
      flex-direction: column;
    }
  }

  .product-grid-item {
    margin-bottom: rem-calc(60);
  }

  .pagination {
    align-self: center;
    margin: rem-calc(50px) 0 rem-calc(80px);
  }
}


/*----------  Sidebar  ----------*/

.listing-sidebar {
  @include media($min: $breakpoint-s) {
    padding-right: $double-gutter;
  }

  .listing-utils {
    float: none;
    padding: $double-gutter $gutter;
    margin: rem-calc(0 -15);
    border-top: 1px solid $color-border;

    @include media($min: $breakpoint-s) {
      display: none;
    }

    .sorting,
    .form-select-wrapper,
    .form-field {
      display: block;
      margin: 0;
    }

    .form-label {
      margin: 0 0 rem-calc(10);
    }

    .form-input {
      width: 100%;
    }
  }
}

.faceted-search-toggle {
  position: relative;
  display: block;
  margin: 1.5em 0;
  text-decoration: none;
  cursor: pointer;

  &::before {
    position: absolute;
    top: rem-calc(-15);
    right: rem-calc(-10);
    bottom: rem-calc(-15);
    left: rem-calc(-10);
    content: "";
  }

  @include media($min: $breakpoint-s) {
    display: none;

    &::before {
      display: none;
    }
  }

  &.is-open {
    .icon-plus {
      display: none;
    }
  }
}

.faceted-search {
  @include media($max: $breakpoint-s) {
    display: none;
  }

  &.visible {
    display: block;
  }
}

.facet-list {
  margin-bottom: 3em;
}

.facet-list-items {
  display: none;

  &.is-open,
  .is-open & {
    display: block;
  }

  .product-item-rating {
    > .icon-star {
      cursor: not-allowed;
    }

    .selected-rating {
      font-family: $font-secondary;
      font-size: rem-calc(15);
      letter-spacing: 0.09375em;
      text-transform: uppercase;
    }

    .icon-star {
      width: rem-calc(18);
      height: rem-calc(18);
    }
  }

  .product-item-rating-facet {
    &:hover {
      .icon-star {
        fill: $color-nav-active;
      }
    }

    &.full {
      .icon-star {
        fill: $color-nav-active;
      }
    }
  }
}

.facet-price {
  display: none;

  &.is-open {
    display: block;
  }
}

.faceted-search-option-columns {
  display: none;

  .is-open & {
    &.visible { display: block; }
  }
}

.facet-list-title {
  position: relative;
  font-size: rem-calc(16px);
  font-weight: normal;
  text-transform: none;
  cursor: pointer;

  &::before {
    position: absolute;
    top: rem-calc(-15);
    right: rem-calc(-10);
    bottom: rem-calc(-15);
    left: rem-calc(-10);
    content: "";
  }

  @include media($min: $breakpoint-s) {
    font-size: rem-calc(14px);

    &::before {
      display: none;
    }
  }
}

.facet-list-toggle {
  @include icon-circle($size: 20px);
  position: relative;
  float: right;
  color: $color-meta;
  cursor: pointer;

  &::before,
  &::after {
    position: absolute;
    display: block;
    background: $color-meta;
    content: "";
  }

  &::before {
    top: rem-calc(3);
    left: rem-calc(7);
    width: rem-calc(2);
    height: rem-calc(10);
    transition: all 0.1s ease;

    .is-open & { display: none; }
  }

  &::after {
    top: rem-calc(7);
    left: rem-calc(3);
    width: rem-calc(10);
    height: rem-calc(2);
  }
}

.facet-item,
.facet-toggle-more {
  margin-bottom: rem-calc(10);
  font-family: $font-secondary;
  font-size: rem-calc(13px);
  letter-spacing: 0.0625em;
  color: $color-meta;
  text-transform: uppercase;

  a {
    color: inherit;
  }

  .icon {
    width: 9px;
    height: 6px;
  }

  &.toggle-link-text-on {
    display: none;
  }
}

.facet-toggle-more {
  display: none;

  .is-open & {
    display: block;
  }
}

// Facet option list expand
.facet-default.is-open {
  display: block;
}

.selected-facet {
  color: $color-accent;
  font-weight: bold;

  a {
    &:after {
      @include icon-close($color-accent);
      display: inline-block;
      width: rem-calc(10);
      height: rem-calc(10);
      margin-left: rem-calc(5);
      content: "";
    }
  }
}

.facet-remove {
  font-size: rem-calc(13px);
}

.facet-price {

  @include media($min: $breakpoint-m) {
    @include layout-group;
    display: none;

    &.is-open {
      display: block;
    }
  }

  .form-actions {
    @include media($min: $breakpoint-xs) {
      @include layout-group-item;
    }

    @include media($min: $breakpoint-s) {
      display: block;
      margin-top: 6%;
    }

    @include media($min: $breakpoint-m) {
      @include layout-group-item;
      margin-top: 0;
      vertical-align: top;
    }
  }

  .button {
    padding-right: 1em;
    padding-left: 1em;

    @include media($min: $breakpoint-xs, $max: $breakpoint-m) {
      width: 94%;
    }
  }

  .form-field {
    @include layout-group-item;
    padding-right: 6%;

    .form-input {
      padding-left: 8%;
      padding-right: 8%;
      text-align: center;
      -moz-appearance: textfield;

      &::-webkit-inner-spin-button,
      &::-webkit-outer-spin-button {
        -webkit-appearance: none;
        margin: 0;
      }
    }
  }

  .form-input {
    width: 100%;
  }

  .icon-loop {
    position: relative;
    top: 1px;
    left: 1px;
    width: rem-calc(16);
    height: rem-calc(16);
  }
}

components/products/product-listing-item:
$card-margin: 10px;

.product-grid {
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  justify-content: center; // Center the grid items
}

.product-grid-item {
  text-align: center;
  margin-bottom: 10px;
  flex: 1 1 calc(50% - #{10px * 2});

  @media (min-width: 1000px) {
    flex: 1 1 calc(33.333% - #{10px * 2});
  }

  @media (min-width: 1200px) {
    flex: 1 1 calc(25% - #{10px * 2});
  }

 .product-item-container {
    padding: 15px;
    margin-left: 10px;
    background-color: #ffffff;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    height: 100%;
    border-radius: 10px;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.1);
    transition: all 0.3s ease-in-out;
    max-width: 300px; // Restrict max width for better appearance on large screens
    position: relative; // Add relative positioning to the container

    &:hover {
      box-shadow: 0 0 15px rgba(0, 0, 0, 0.2);
      background-color: #c3e7d5;
    }

    .product-item-thumbnail,
    .product-item-details,
    .button {
      margin-bottom: 15px;
    }

    .product-item-thumbnail {
      margin-bottom: rem-calc(10); // Reduce bottom margin
      overflow: hidden;
      height: auto;
      position: relative; // Ensure the thumbnail is relative for absolute positioning of icons

      @media (max-width: $breakpoint-m) {
        height: 150px; // Ad just height for smaller screens
      }

      @media (max-width: $breakpoint-s) {
        height: 120px; // Adjust height for the smallest screens
      }

      a {
        display: block;
        height: 100%;
      }

      img {
        width: 100%;
        height: 100%;
        display: block;
        object-fit: cover; // Changed to cover for better image fitting
        border-radius: 5px; // Rounded corners for a more polished look
      }
    }

    .quick-view-icon1,
    .recycle-icon {
      position: absolute;
      top: 2px; // Adjust top position
      padding: 2px; // Adjust padding to balance icon size
      background-color: transparent; // Slightly transparent background
      border-radius: 50%; // Circular icon background
    }

    .quick-view-icon1 {
      left: 2px; // Adjust left position

      svg {
        width: 18px; // Smaller icon for mobile
        height: 18px; // Smaller icon for mobile
        stroke: #333333;
        fill: none;
        transition: stroke 0.2s ease-in-out;
      }

      &:hover {
        transform: scale(1.1);

        svg {
          stroke: #ff6600;
        }
      }
    }

    .recycle-icon {
      right: 2px; // Adjust right position

      img {
        width: 18px; // Smaller icon for mobile
        height: 18px; // Smaller icon for mobile
      }
    }

    .product-item-details {
      flex-grow: 1;
      display: flex;
      flex-direction: column;
      justify-content: space-between;
      text-align: left;
      padding: 0 15px;
      width: 100%;

      @media (max-width: $breakpoint-s) {
        padding: 0 15px; // Reduce padding for small screens
      }

      .product-item-brand1,
      .product-item-brand2,
