pages/compare.scss:
/*----------  Top-bar Tab  ----------*/

.compare-wrap {
  position: relative;
  display: none;
  height: $topbar-height;
  vertical-align: top;

  &.compare-active {
    display: inline-block;
  }

  &:hover {
    .compare-tab {
      opacity: 1;
      visibility: visible;
      transform: translate(-50%, 0);
    }
  }
}

.button-compare-toggle {
  @extend %top-bar-font;
  position: relative;
  height: $topbar-height;
  padding-top: rem-calc(6px);
  padding-right: rem-calc(47px);
  line-height: 0;
}

.compare-items-count {
  position: absolute;
  top: 17px;
  right: $gutter;
  display: inline-block;
  width: 24px;
  height: 24px;
  line-height: 24px;
  color: $color-topbar-background;
  text-align: center;
  background-color: $color-accent;
  border-radius: 50%;
}

.compare-tab {
  position: absolute;
  top: 100%;
  left: 3px;
  width: 410px;
  height: 90vh;
  padding: $double-gutter 0;
  overflow: scroll;
  text-align: center;
  background-color: $color-mini-cart-background;
  opacity: 0;
  visibility: hidden;
  transform: translate(-50%, -15px);
  transition: all 0.3s ease;

  @include media($min: $breakpoint-m) {
    left: 50%;
  }
}

.compare-items-list {
  margin-top: $gutter;
}

.compare-tab-item {
  @extend .mini-cart-item;
  display: none;

  &.animating,
  &.visible {
    display: block;
  }

  &.animating-in,
  &.animating-out {
    transform: translateY(-10%);
    opacity: 0;
    transition: opacity 0.2s ease, transform 0.3s ease;
  }

  &.visible {
    transform: translateY(0);
    opacity: 1;
  }
}

.compare-tab-item-wrap {
  @extend .mini-cart-item-wrap;
}

.compare-tab-item-thumb {
  width: 75px;
  padding-bottom: 75px;
  background-position: center center;
  background-size: contain;
  background-repeat: no-repeat;
}

.compare-tab-item-description {
  @extend .mini-cart-item-description;
  padding-right: $gutter;
  text-align: left;
}

.compare-tab-item-title {
