.compare-table-heading {
  @extend %body-text-treatment;
  width: 20%;
  padding-left: 0;
  text-align: left;
  text-transform: none;
  font-weight: normal;
  vertical-align: top;
}

.compare-table-cell {
  min-width: 230px;
  vertical-align: top;
}

.compare-remove {
  display: block;
  margin-top: $gutter;
}

