  margin: 0;
}

.billing-addess-form {
  padding-top: $account-gutter-inner;
  border-top: 1px solid $account-border-color;

  .form-field {
    width: 100%;

    @media screen and (min-width: $account-breakpoint-x-small) {
      float: left;
      width: 50%;
      padding-right: 7.5px;

      &:nth-of-type(2n + 1) {
        clear: left;
      }
    }
  }
}

.billing-actions {
  .button {
    +.button {
      margin-top: 20px;

      @media screen and (min-width: $account-breakpoint-x-small) {
        margin-top: 0;
        margin-left: 5px;
      }
    }
  }
}

