core/account/orders/all:
{{#partial 'account-heading'}}
  {{lang 'core.account.orders.heading_all'}}
{{/partial}}

{{#partial 'account-content'}}
  {{> core/account/_private/orders-list orders=customer.orders}}
  {{> core/pagination pagination.orders}}
{{/partial}}

{{> core/account/_private/layout nav_page='orders' class='orders-all'}}

core/account/_private/orders-list:
{{#if orders}}
  <div class="account-items">
    {{#each orders}}
      <article class="account-item">
        <a class="account-item-link" href="{{details_url}}"></a>

        <div class="account-item-thumbnail">
          <a href="{{details_url}}">
            {{#if items.0.type '===' 'giftcertificate'}}
              <img src="{{cdn ../../theme_settings.default_image_gift_certificate}}">
            {{else}}
              {{> components/common/responsive-image
                image=image
                fit=../../theme_settings.product_grid_image_cropping
                lazyload=../../theme_settings.lazyload_mode
                fallback_size=../../theme_settings.account-product-thumbnail
                default_image=../../theme_settings.default_image
              }}
            {{/if}}
          </a>
        </div>

        <div class="account-item-body">
          <h1 class="account-item-title">
            <a href="{{details_url}}">
              {{lang 'core.account.orders.items.number' number=id}}
            </a>
          </h1>

          <p class="account-item-description">
            {{lang 'core.account.orders.items.description' num_products=items.length cost=total.formatted}}
          </p>

          <div class="account-item-details">
            <div class="account-item-detail">
              <h2 class="account-item-detail-title">
                {{lang 'core.account.orders.items.placed'}}
              </h2>
              <span class="account-item-detail-value">
                {{date}}
              </span>
            </div>

            {{#if date '!==' last_update_date}}
              <div class="account-item-detail">
                <h2 class="account-item-detail-title">
                  {{lang 'core.account.orders.items.updated'}}
                </h2>
                <span class="account-item-detail-value">
                  {{last_update_date}}
                </span>
              </div>
            {{/if}}
          </div>
        </div>

        <div class="account-item-badge">
          <h2 class="account-status">
            {{status}}
          </h2>
        </div>
      </article>
    {{/each}}
  </div>

{{else}}
  <div class="account-empty">
    {{lang 'core.account.orders.items.empty'}}
  </div>
{{/if}}

core/account/_private/layout:
{{!
  A wrapper for all account pages.

  @block account-heading
         The page's title.

  @block account-content
         The content for the main part of the layout.

  @param class string
         A name for the current page, added as a class to the account wrapper.

  @param nav string
         If "hidden", no nav will be shown.

  @param nav_page string
         If matches a string in navigation.html, that link will be made active.

  @param header string
         If "hidden", no header will be shown.
         If "top", header will be shown above the nav.
         Otherwise, header will be shown above the content.
}}
<section class="account-wrapper account-page-{{class}}{{#if nav '===' 'hidden'}} account-nav-hidden{{/if}}">
  {{#if header '===' 'top'}}{{> core/account/_private/layout-heading}}{{/if}}

  {{#if nav '!==' 'hidden'}}
    {{> core/account/_private/navigation nav_page=nav_page}}
  {{/if}}

  <div class="account-wishlist-wrapper">
    <div class="account-body">
        {{#unless header}}{{> core/account/_private/layout-heading}}{{/unless}}
        <div class="account-content">
        {{#block 'account-content'}}{{/block}}
        </div>
    </div>
    {{> components/common/pagination wishlist.pagination}}
  </div>
</section>
