components/brand/product-listing-list:
{{#if brand.products}}
  <div class="listing-wrapper list-view">
    {{#each brand.products}}
      {{> components/products/product-list-item
        this
        show_rating=../settings.show_product_rating
        show_compare=../category.show_compare
        image_ratio=../theme_settings.product_grid_image_ratio
        fit=../theme_settings.product_grid_image_cropping
        lazyload=../theme_settings.lazyload_mode
        fallback_size=../theme_settings.thumb
        default_image=../theme_settings.default_image
        quick_shop=../theme_settings.quick-shop
        show_sale_badges=../theme_settings.sale-badges
        price_range_display=../theme_settings.price_range_display
        retail_price_label=../theme_settings.retail-price-label
        non_sale_price_label=../theme_settings.non-sale-price-label
        sale_price_label=../theme_settings.sale-price-label
        event="list"
        position=(add @index 1)
      }}
    {{/each}}
  </div>

  {{> components/common/pagination pagination.brand}}
{{else}}
  <div>
    {{lang 'category.empty'}}
  </div>
{{/if}}

components/products/product-list-item:
{{!--
  Product Grid Item - As displayed within a grid listing of products.

  @param product object
    The product object to display, handed over from the loop

  @param show_compare bool
    Whether to show "compare" checkboxes

  @param quick_shop theme-setting
    Theme setting to enable / disable quickshop

  @param image_ratio theme-setting
    The image ratio for this grid

  @param default theme-setting
    The default image if none exists

  @param show_rating platform-setting
    Platform setting to toggle ratings

  @param as_low_as theme-setting
    The setting to show/hide as low as price wording
--}}

<article
  class="product-list-item product-block"
  data-product-title="{{name}}"
  data-product-id="{{id}}"
  data-event-type="{{event}}"
  data-entity-id="{{id}}"
  data-position="{{position}}"
  data-name="{{name}}"
  data-product-category="{{#each category}}{{#if @last}}{{this}}{{else}}{{this}}, {{/if}}{{/each}}"
  data-product-brand="{{brand.name}}"
  data-product-price="{{#if price.with_tax}}{{price.with_tax.value}}{{else}}{{price.without_tax.value}}{{/if}}"
>
  <div class="product-thumb-wrapper">
    <figure class="product-item-thumbnail {{image_ratio}}">
      {{#or price.non_sale_price_with_tax price.non_sale_price_without_tax}}
      {{#unless out_of_stock_message}}
        {{#if show_sale_badges}}
          <div class="product-badge">
            {{lang 'product.sale'}}
          </div>
          {{/if}}
        {{/unless}}
      {{/or}}
      {{#if out_of_stock_message}}
        <div class="product-badge sold-out">
          <span class="message">
            {{out_of_stock_message}}
          </span>
        </div>
      {{/if}}
      {{> components/common/responsive-image
        image=image
        class="product-item-image"
        fit=fit
        fallback_size=fallback_size
        lazyload=lazyload
        default_image=default_image
      }}
    </figure>
  </div>

  <div class="product-item-details">
    {{#if brand.name}}
      <div class="product-item-brand">{{brand.name}}</div>
    {{/if}}
    <h5 class="product-item-title">
      <a href="{{url}}" alt="{{name}}">{{name}}</a>
    </h5>

    {{#if show_rating}}
      <p class="product-item-rating">
        <span class="rating-title">{{rating}} {{lang 'product.stars'}}</span>
        {{> components/products/ratings rating=rating}}
      </p>

      {{{snippet 'product_rating'}}}
    {{/if}}

    <div class="product-description">
      {{{summary}}}
    </div>
  </div>

  <div class="product-item-actions">
    <div class="product-item-price" data-product-price="{{price.without_tax.formatted}}">
      {{> core/products/price
        price=price
        show_tax_labels=true
        show_sale_badges=false
        price_range_display=price_range_display
        retail_price_label=retail_price_label
        non_sale_price_label=non_sale_price_label
        sale_price_label=sale_price_label
        price_label=price_label
      }}
    </div>

    {{#if quick_shop}}
      <button class="button button-secondary button-short button-wide" data-quick-shop data-product-id="{{id}}">
        {{lang 'product.quick_view'}}
      </button>
    {{/if}}

    {{#if show_compare}}
      <label class="button button-short button-wide form-label product-item-compare" for="compare-{{id}}">
        {{> components/common/compare-checkbox}}
        <span class="form-label-text">
          {{lang "product.compare"}}
          <svg class="icon icon-check-mark"><use xlink:href="#icon-check-mark" /></svg>
        </span>
      </label>
    {{/if}}
  </div>
</article>

core/products/price:
{{!
  Show product prices

  Prices are fairly complicated. Besides having to be able to show the price
  with and without tax, a merchant can also assign a Recommended Retail Price,
  and a Sale Price.

  This means that in extreme cases, we have to show 4 different prices at once.

  When a Sale Price is set we do not have direct access to it--instead it
  overwrites the regular price.

  @param price object

  @param show_tax_labels bool
    If true, show a label saying the price is with or without tax.

  @param show_savings bool
    If true, show a label saying how much is saved from the full price.

  @param show_sale_badges bool
    If true, show sale badges inline with price.

  @param sale_badge_classes string
    Class to append to sale badge

  @param price_range_display string
    If set to "both", show price as a range when applicable.

  @param show_amp_classes bool
    If true include amp class component
}}
{{#if price}}
   {{#all (if price_range_display '===' 'both') price.price_range}}
    {{> core/products/price-range
      price_range=price.price_range
      show_amp_classes=show_amp_classes
    }}
  {{else}}
    <div
      class="
        price
        {{#all price.without_tax price.with_tax}}multiple-prices{{/all}}
        {{#if show_amp_classes}}
          {{> core/amp/common/price-class}}
        {{/if}}
        "
      >

      {{#if price.without_tax}}
        <div
          class="price-withouttax"
          data-product-price-wrapper="without-tax"
        >

          {{#if price.rrp_without_tax}}
            <div class="price-rrp-wrapper">
              <label class="price-rrp-label">
                {{retail_price_label}}
              </label>
              <span class="price-rrp" data-price-rrp>
                {{price.rrp_without_tax.formatted}}
              </span>
            </div>
          {{/if}}

          {{#if price.non_sale_price_without_tax}}
            <div class="price-ns-wrapper">
              <label class="price-ns-label">
                {{non_sale_price_label}}
              </label>
              <span class="price-ns" data-price-rrp>
                {{price.non_sale_price_without_tax.formatted}}
              </span>
            </div>
          {{/if}}

          <div class="price-value-wrapper">
            {{!-- If there is another price show "now" label --}}
            {{#if price.non_sale_price_without_tax}}
              <label class="price-value-label">
                {{sale_price_label}}
              </label>
            {{else}}
              <label class="price-label">
                {{price_label}}
              </label>
            {{/if}}
            <span class="price-value" data-product-price>
              {{price.without_tax.formatted}}
            </span>

            {{#if show_tax_labels}}
              {{#all price.with_tax price.without_tax}}
                <span class="price-tax-label">
                  {{lang
                    'core.price.without_tax'
                    tax_label=price.tax_label
                  }}
                </span>
              {{/all}}
            {{/if}}

            {{#all show_sale_badges (unless price.with_tax)}}
              {{> core/products/sale-badge
                classes=sale_badge_classes
              }}
            {{/all}}
          </div>

        </div>
      {{/if}}

      {{#if price.with_tax}}
        <div
          class="price-withtax"
          data-product-price-wrapper="with-tax"
        >

          {{#if price.rrp_with_tax}}
            <div class="price-rrp-wrapper">
              <label class="price-rrp-label">
                {{retail_price_label}}
              </label>
              <span class="price-rrp" data-price-rrp>
                {{price.rrp_with_tax.formatted}}
              </span>
            </div>
          {{/if}}

          {{#if price.non_sale_price_with_tax}}
            <div class="price-ns-wrapper">
              <label class="price-ns-label">
                {{non_sale_price_label}}
              </label>
              <span class="price-ns" data-price-rrp>
                {{price.non_sale_price_with_tax.formatted}}
              </span>
            </div>
          {{/if}}

          <div class="price-value-wrapper">
            {{!-- If there is another price show "now" label --}}
            {{#if price.non_sale_price_with_tax}}
              <label class="price-value-label">
                {{sale_price_label}}
              </label>
            {{else}}
              <label class="price-label">
                {{price_label}}
              </label>
            {{/if}}
            <span class="price-value" data-product-price>
              {{price.with_tax.formatted}}
            </span>

            {{#if show_tax_labels}}
              {{#all price.with_tax price.without_tax}}
                <span class="price-tax-label">
                  {{lang
                    'core.price.with_tax'
                    tax_label=price.tax_label
                  }}
                </span>
              {{/all}}
            {{/if}}

            {{#if show_sale_badges}}
              {{> core/products/sale-badge
                classes=sale_badge_classes
              }}
            {{/if}}
          </div>
        </div>
      {{/if}}

      {{#if show_savings}}
        {{#if price.saved}}
          <span class="price-saved" data-product-price-saved>
            {{lang 'core.price.you_save' amount=price.saved.formatted}}
          </span>
        {{/if}}
      {{/if}}
    </div>
  {{/all}}
{{/if}}
{{#if call_for_price}}
  <div class="price call-for-price">
    {{call_for_price}}
  </div>
{{/if}}

core/amp/common/price-class:
{{#if price.with_tax}}
  {{#unless price.without_tax}}
    {{#if price.rrp_with_tax}}
      prices-taxed-onsale
    {{else}}
      prices-taxed
    {{/if}}
  {{/unless}}
{{/if}}
{{#if price.without_tax}}
  {{#unless price.with_tax}}
    {{#if price.rrp_without_tax}}
      prices-notax-onsale
    {{else}}
      prices-notax
    {{/if}}
  {{/unless}}
{{/if}}
{{#if price.with_tax}}
  {{#if price.without_tax}}
    {{#if price.rrp_with_tax}}
      prices-both-onsale
    {{else}}
      prices-both
    {{/if}}
  {{/if}}
{{/if}}

core/products/price-range:
{{!
  Shows a product's price range.

  @param price_range object

  @param show_amp_classes bool
    If true include amp class component
}}
<div
  class="
    price
    {{#if show_amp_classes}}
      {{> core/amp/common/price-class}}
    {{/if}}
  "
>
  {{#and price_range.min.with_tax price_range.max.with_tax}}
    <div class="price-withtax" data-product-price-wrapper="with-tax">
      <span data-product-price-with-tax class="price price-value">
        {{price_range.min.with_tax.formatted}} - {{price_range.max.with_tax.formatted}}
      </span>
      {{#and price_range.min.without_tax price_range.max.without_tax}}
        <span class="price-tax-label">
          {{lang
            'core.price.with_tax'
            tax_label=price.tax_label
          }}
        </span>
      {{/and}}
    </div>
  {{/and}}
  {{#and price_range.min.without_tax price_range.max.without_tax}}
    <div class="price-withouttax" data-product-price-wrapper="without-tax">
      <span data-product-price-without-tax class="price price-without-tax">
        {{price_range.min.without_tax.formatted}} - {{price_range.max.without_tax.formatted}}
      </span>
      {{#and price_range.min.with_tax price_range.max.with_tax}}
        <span class="price-tax-label">
          {{lang
            'core.price.without_tax'
            tax_label=price.tax_label
          }}
        </span>
      {{/and}}
    </div>
  {{/and}}

  <span class="price-saved" data-product-price-saved></span>
</div>

core/products/sale-badge:
{{!
  Shows a sale badge if a sale price has been entered

  Themes can use this to display a sale badge.

  @param classes string
    Class(es) to append to sale badge
}}
{{#or price.non_sale_price_with_tax price.non_sale_price_without_tax}}
  <span class="sale-badge {{classes}}">
    {{lang 'core.product.sale'}}
  </span>
{{/or}}
